import React, { useState, useEffect } from "react";
import "./App.css";
import { HubConnectionBuilder } from "@microsoft/signalr";

function App() {
    const [tickets, setTickets] = useState([]);
    const [hubConnection, setHubConnection] = useState(null);

    useEffect(() => {
        // Inicjalizowanie po��czenia SignalR
        const connection = new HubConnectionBuilder()
            .withUrl("http://localhost:5000/hubs/tickets") // Upewnij si�, �e URL jest poprawny
            .withAutomaticReconnect()
            .build();

        connection.start().catch(err => console.error("SignalR error: ", err));

        // Nas�uchiwanie zdarze� SignalR
        connection.on("TicketCreated", (ticket) => {
            setTickets(prevTickets => [...prevTickets, ticket]);
        });

        connection.on("TicketUpdated", (updatedTicket) => {
            setTickets(prevTickets =>
                prevTickets.map(ticket => (ticket.id === updatedTicket.id ? updatedTicket : ticket))
            );
        });

        // Przypisanie po��czenia SignalR
        setHubConnection(connection);

        // Zwr�cenie funkcji sprz�taj�cej w przypadku odmontowania komponentu
        return () => {
            connection.stop();
        };
    }, []);

    // Pobieranie zg�osze� z backendu
    useEffect(() => {
        fetch("http://localhost:5000/api/tickets")
            .then(response => response.json())
            .then(data => setTickets(data));
    }, []);

    return (
        <div className="App">
            <h1>System Zarz�dzania Zg�oszeniami</h1>
            <div>
                <h2>Lista Zg�osze�</h2>
                <ul>
                    {tickets.map(ticket => (
                        <li key={ticket.id}>
                            <h3>{ticket.title}</h3>
                            <p>{ticket.description}</p>
                            <p>Status: {ticket.status}</p>
                            <p>Priorytet: {ticket.priority}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default App;
